mapper.update:
update reservation
set acceptance = -2
where reserveNo = #{reserveNo}

update reservation
set acceptance = 1
where reserveNo = #{reserveNo}

update reservation
set acceptance = 2
where reserveNo = #{reserveNo}

mapper.select:
select count(*)
from reservation
where acceptance = 2 and hosid = #{hosid}

select count(*)
from reservation
where hosid = #{hosid}
